SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_seq_apres_adep (ie_grupo_p text, nr_regra_p bigint, ie_status_item_p text default null) RETURNS bigint AS $body$
DECLARE


nr_seq_apres_w	integer := 999;
ie_regra_w	varchar(1);
ie_grupo_w	varchar(10);
ie_ordena_suspenso_w		varchar(1);


BEGIN
ie_ordena_suspenso_w := Obter_Param_Usuario(950, 135, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_ordena_suspenso_w);

if (nr_regra_p 	> 0) and (ie_grupo_p IS NOT NULL AND ie_grupo_p::text <> '') then
	begin
	select 	coalesce(max('S'),'N')
	into STRICT	ie_regra_w
	from	regra_ordem_rep
	where	nr_sequencia = nr_regra_p;

	if (ie_regra_w = 'S') then
		begin

		ie_grupo_w	:= ie_grupo_p;
		if (ie_grupo_p  = 'DE') then
			ie_grupo_w	:= 'D';
		end if;

		select	coalesce(max(nr_seq_apres),999)
		into STRICT	nr_seq_apres_w
		from	regra_ordem_grupo_rep
		where	nr_seq_regra = nr_regra_p
		and     ((ie_grupo = ie_grupo_w) or
			 (ie_grupo_p = 'C' AND ie_grupo = 'G'));

		if (ie_ordena_suspenso_w = 'S')	and (coalesce(ie_status_item_p,'N') = 'S')	then

			select	coalesce(max(nr_seq_apres),999) + 1
			into STRICT	nr_seq_apres_w
			from	regra_ordem_grupo_rep
			where	nr_seq_regra = nr_regra_p;

		end if;

		end;

	end if;

	end;
elsif (ie_ordena_suspenso_w	= 'S') and (coalesce(ie_grupo_p::text, '') = '') then
	nr_seq_apres_w	:= 0;
end if;

return nr_seq_apres_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
