CREATE OR REPLACE FUNCTION obter_nr_seq_assinatura_cpoe (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_assinatura_w	bigint := null;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select  max(b.nr_seq_assinatura)
	into STRICT	nr_seq_assinatura_w
	from 	prescr_procedimento a,
			cpoe_procedimento b
	where 	a.nr_seq_proc_cpoe = 	b.nr_sequencia
	and 	a.nr_prescricao = nr_prescricao_p;

	if (coalesce(nr_seq_assinatura_w::text, '') = '') then

		select  max(b.nr_seq_assinatura)
		into STRICT	nr_seq_assinatura_w
		from 	prescr_procedimento a,
				cpoe_recomendacao b
		where 	a.nr_seq_proc_cpoe = 	b.nr_sequencia
		and 	a.nr_prescricao = nr_prescricao_p;

	end if;

	if (coalesce(nr_seq_assinatura_w::text, '') = '') then

		select  max(b.nr_seq_assinatura)
		into STRICT	nr_seq_assinatura_w
		from 	prescr_procedimento a,
				cpoe_material b
		where 	a.nr_seq_proc_cpoe = 	b.nr_sequencia
		and 	a.nr_prescricao = nr_prescricao_p;

	end if;

end if;

return	nr_seq_assinatura_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
