CREATE OR REPLACE FUNCTION obter_nr_seq_exame (nr_seq_proc_interno_p bigint, nr_atendimento_p bigint, nr_seq_rotina_p bigint) RETURNS bigint AS $body$
DECLARE

		
nr_seq_exame_w	proc_interno.nr_seq_exame_lab%type;
cd_estabelecimento_w	atend_paciente_unidade.cd_setor_atendimento%type;


BEGIN
	cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento,1);
	select max(nr_seq_exame_lab)
			into STRICT nr_seq_exame_w
	from proc_interno
	where nr_sequencia = nr_seq_proc_interno_p;

	if (nr_seq_rotina_p IS NOT NULL AND nr_seq_rotina_p::text <> '') then
		select max(a.nr_seq_exame)
			into STRICT nr_seq_exame_w 
		from exame_lab_rotina a
		where a.nr_sequencia = nr_seq_rotina_p
		  and (coalesce(a.nr_seq_exame_interno::text, '') = '' or a.nr_seq_exame_interno = nr_seq_proc_interno_p);
	end if;

	if (coalesce(nr_seq_exame_w::text, '') = '') then 
		select max(a.nr_seq_exame) 
			into STRICT nr_seq_exame_w 
		from exame_laboratorio a 
		where a.cd_estabelecimento = cd_estabelecimento_w
		and a.nr_seq_proc_interno = nr_seq_proc_interno_p;
	end if;

	return nr_seq_exame_w;
end;
