CREATE OR REPLACE FUNCTION obter_nr_seq_grupo_sup_des (ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;


BEGIN

if (ie_opcao_p = 'S') then

	select	max(nr_seq_grupo)
	into STRICT	ds_retorno_w
	from	usuario_grupo_sup
	where	nm_usuario_grupo = wheb_usuario_pck.get_nm_usuario;

elsif (ie_opcao_p = 'D') then

	select	max(nr_seq_grupo)
	into STRICT	ds_retorno_w
	from	usuario_grupo_des
	where	nm_usuario_grupo = wheb_usuario_pck.get_nm_usuario;

end if;

return	ds_retorno_w;

end;
$body$
