CREATE OR REPLACE FUNCTION obter_nr_seq_interno_adep ( nr_prescricao_p bigint, ie_tipo_item_p text, cd_item_p bigint, cd_intervalo_p text, nr_seq_item_p bigint, qt_item_p bigint, nr_prescricoes_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w	bigint;
nr_prescricao_w		bigint;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') or (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	begin

	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		begin
		nr_prescricao_w	:= nr_prescricao_p;
		end;
	else
		begin
		nr_prescricao_w	:= obter_max_nrprescricao(nr_prescricoes_p);
		end;
	end if;

	if (ie_tipo_item_p = 'S') or (ie_tipo_item_p = 'SNE') or (ie_tipo_item_p = 'M') or (ie_tipo_item_p = 'MAT') or (ie_tipo_item_p = 'IAH') then
		begin
		select	coalesce(max(nr_seq_interno),0)
		into STRICT	nr_seq_interno_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_w
		and	coalesce(qt_dose,0) = coalesce(qt_item_p,coalesce(qt_dose,0))
		and	cd_material = cd_item_p
		and	coalesce(cd_intervalo,'XPTO') = coalesce(cd_intervalo_p,'XPTO');
		end;
	elsif (ie_tipo_item_p = 'SOL') then
		begin
		select	coalesce(max(nr_seq_interno),0)
		into STRICT	nr_seq_interno_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_w
		and	nr_seq_solucao	= nr_seq_item_p;
		end;
	elsif (ie_tipo_item_p = 'HM') or (ie_tipo_item_p = 'P') then
		begin
		select	coalesce(max(nr_seq_interno),0)
		into STRICT	nr_seq_interno_w
		from	prescr_procedimento
		where	nr_prescricao 	= nr_prescricao_w
		and	nr_sequencia	= nr_seq_item_p;
		end;
	end if;
	end;
end if;
return	nr_seq_interno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
