SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_seq_locale (nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


retorno_w	bigint;
tag_pais_w	varchar(15);
cd_estabelicimento_w	bigint;


BEGIN

select	max(ds_locale)
into STRICT	tag_pais_w
from	user_locale
where	nm_user = nm_usuario_p;

if (coalesce(tag_pais_w::text, '') = '')then
	select	max(cd_estabelecimento)
	into STRICT 	cd_estabelicimento_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select 	max(ds_locale)
	into STRICT	tag_pais_w
	from	establishment_locale
	where	cd_estabelecimento = cd_estabelicimento_w;
end if;

select	max(nr_sequencia)
into STRICT 	retorno_w
from	tasy_idioma
where	ds_language_tag = tag_pais_w;

return	coalesce(retorno_w, 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
