CREATE OR REPLACE FUNCTION obter_nr_seq_mes_ref (cd_estab_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	ctb_mes_ref r
where	r.cd_empresa = obter_empresa_estab(cd_estab_p)
and	r.dt_referencia = trunc(dt_referencia_p,'mm')
and	substr(ctb_obter_se_mes_fechado(nr_sequencia,0),1,1) = 'A';

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
