CREATE OR REPLACE FUNCTION obter_nr_seq_protoc_lote_opme ( nr_lote_p bigint, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w	varchar(1) := 'S';


BEGIN

if (nr_lote_p IS NOT NULL AND nr_lote_p::text <> '') and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	select coalesce(max('S'),'N')
	into STRICT	ie_exibe_w
	from 	lote_producao_comp a,
		protocolo_fornec_opme b
	where 	nr_seq_protocolo 	= b.nr_sequencia
	and  	a.nr_lote_producao 	= nr_lote_p
	and	a.nr_seq_protocolo 	= nr_seq_protocolo_p;
end if;

return ie_exibe_w;

end;
$body$
