CREATE OR REPLACE FUNCTION obter_nr_telefone_int ( nr_ddi_p text, nr_telefone_p text, nr_ddd_telefone_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);
nr_ddi_w		varchar(255);


BEGIN

if (nr_telefone_p IS NOT NULL AND nr_telefone_p::text <> '') then

	if (substr(nr_ddi_p,1,1) = '+') then
		nr_ddi_w := nr_ddi_p;
	else
		nr_ddi_w := '+' || nr_ddi_p;
	end if;

	if (nr_ddd_telefone_p IS NOT NULL AND nr_ddd_telefone_p::text <> '') then
		ds_retorno_w := '(' || nr_ddd_telefone_p || ') ' || nr_telefone_p;
	else
		ds_retorno_w := nr_telefone_p;
	end if;

	if (nr_ddi_p IS NOT NULL AND nr_ddi_p::text <> '') then
		ds_retorno_w := nr_ddi_w || ' ' || ds_retorno_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
