SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_titulo_conta_prot (nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_titulo_w					bigint;	
nr_seq_protocolo_w			bigint;
nr_seq_lote_protocolo_w		bigint;

BEGIN
 
select	max(nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_receber a 
where (nr_interno_conta_p <> 0) 
and (nr_interno_conta = nr_interno_conta_p);
 
if (coalesce(nr_titulo_w::text, '') = '') then 
	 
	select	max(c.nr_seq_protocolo), 
			max(x.nr_seq_lote_protocolo) 
	into STRICT	nr_seq_protocolo_w, 
			nr_seq_lote_protocolo_w 
	from	conta_paciente c, 
			protocolo_convenio x 
	where  c.nr_interno_conta = nr_interno_conta_p 
	and 	x.nr_seq_protocolo = c.nr_seq_protocolo;
	 
	select	max(nr_titulo) 
	into STRICT	nr_titulo_w 
	from 	titulo_receber a 
	where 	((nr_seq_protocolo_w <> 0 AND nr_seq_protocolo = nr_seq_protocolo_w) 
	or		(nr_seq_lote_protocolo_w <> 0 AND nr_seq_lote_prot = nr_seq_lote_protocolo_w));
	 
end if;
 
return	nr_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nr_titulo_conta_prot (nr_interno_conta_p bigint) FROM PUBLIC;

