CREATE OR REPLACE FUNCTION obter_numero_episodio_integr (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_episodio_w	varchar(25);


BEGIN

select	lpad(coalesce(b.NR_EPISODIO, b.NR_SEQUENCIA),10,0)
into STRICT nr_episodio_w
from	atendimento_paciente a, episodio_paciente b
where	b.nr_sequencia = a.nr_seq_episodio
and	a.nr_atendimento = nr_atendimento_p;

return	nr_episodio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
