CREATE OR REPLACE FUNCTION obter_numero_negociacao_hist ( nr_titulo_p bigint, nr_seq_tit_rec_liq_p bigint, nr_seq_caixa_rec_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(20)	:= null;
nr_seq_negociacao_w	bigint;


BEGIN 
if (nr_seq_tit_rec_liq_p IS NOT NULL AND nr_seq_tit_rec_liq_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	select	max(a.nr_seq_negociacao_cr) 
	into STRICT	nr_seq_negociacao_w 
	from	titulo_receber_liq	a 
	where	a.nr_titulo	= nr_titulo_p 
	and	a.nr_sequencia	= nr_seq_tit_rec_liq_p;
	 
	select	to_char(max(nr_negociacao)) 
	into STRICT	ds_retorno_w 
	from	negociacao_cr 
	where	nr_sequencia	= nr_seq_negociacao_w;
end if;
 
if (nr_seq_caixa_rec_p IS NOT NULL AND nr_seq_caixa_rec_p::text <> '') and (coalesce(ds_retorno_w::text, '') = '') then 
	select	max(b.nr_seq_negociacao_origem) 
	into STRICT	nr_seq_negociacao_w 
	from	titulo_receber		b, 
		titulo_receber_liq	a 
	where	a.nr_titulo		= b.nr_titulo 
	and	a.nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
	 
	select	to_char(max(nr_negociacao)) 
	into STRICT	ds_retorno_w 
	from	negociacao_cr 
	where	nr_sequencia	= nr_seq_negociacao_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
