CREATE OR REPLACE FUNCTION obter_numero_parcela_cartao ( nr_seq_movto_p bigint, nr_seq_parcela_p bigint) RETURNS bigint AS $body$
DECLARE


nr_parcela_w		bigint	:= 1;
nr_seq_parcela_w	bigint;
qt_parcela_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	movto_cartao_cr_parcela a
where	a.nr_seq_movto	= nr_seq_movto_p
order by	a.dt_parcela;


BEGIN

select	count(*)
into STRICT	qt_parcela_w
from	movto_cartao_cr_parcela a
where	a.nr_seq_movto	= nr_seq_movto_p
and	a.nr_sequencia	= nr_seq_parcela_p;

if (qt_parcela_w > 0) then

	open c01;
	loop
	fetch	c01 into
		nr_seq_parcela_w;
	exit	when(c01%notfound) or (nr_seq_parcela_w = nr_seq_parcela_p);

		nr_parcela_w	:= nr_parcela_w + 1;

	end loop;
	close c01;

else
	nr_parcela_w	:= 0;

end if;

return	nr_parcela_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
