CREATE OR REPLACE FUNCTION obter_numero_sangue_seq (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sangue_inicial_w	varchar(20);
nr_sangue_ult_w		varchar(20);
ds_retorno_w		varchar(20);
qt_sangue_ult_w		integer;
qt_sangue_inic_w	integer;



BEGIN

select 	max(somente_numero(nr_sangue_inicial)),
	max(length(nr_sangue_inicial))
into STRICT	nr_sangue_inicial_w,
	qt_sangue_inic_w
from	san_parametro
where	cd_estabelecimento = cd_estabelecimento_p;

select 	max(somente_numero(nr_sangue))
into STRICT	nr_sangue_ult_w
from	san_doacao;


if ( somente_numero(nr_sangue_ult_w) >= somente_numero(nr_sangue_inicial_w) ) then

	qt_sangue_ult_w := length(nr_sangue_ult_w);
	/*select 	length(nr_sangue)
	into	qt_sangue_ult_w
	from	san_doacao
	where	somente_numero(nr_sangue) = nr_sangue_ult_w;*/
	if (length(nr_sangue_ult_w + 1) > qt_sangue_ult_w ) then
		qt_sangue_ult_w := length(nr_sangue_ult_w + 1);
	end if;

	ds_retorno_w := lpad(nr_sangue_ult_w + 1,qt_sangue_ult_w,'0');
else
	ds_retorno_w := lpad(nr_sangue_inicial_w,qt_sangue_inic_w,'0');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
