CREATE OR REPLACE FUNCTION obter_numero_separado ( ds_conteudo_p text, qt_separador_p bigint) RETURNS varchar AS $body$
DECLARE



qt_conteudo_w	bigint;
qt_separador_w	bigint;
qt_inicio_w	bigint := 0;

ds_retorno_w	varchar(4000);


BEGIN

qt_conteudo_w := length(ds_conteudo_p);
qt_separador_w := qt_separador_p;

while qt_conteudo_w >= qt_separador_w loop
	begin
	ds_retorno_w := ds_retorno_w || substr(ds_conteudo_p, (qt_inicio_w + 1), qt_separador_p) || ',';

	qt_inicio_w := qt_separador_w;
	qt_separador_w := qt_separador_w + qt_separador_p;
	end;
end loop;

ds_retorno_w := substr(ds_retorno_w, 1, length(ds_retorno_w)-1);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
