CREATE OR REPLACE FUNCTION obter_numero_seq_funcao_ordem (nr_seq_grupo_p bigint, nr_seq_grupo_trabalho_p bigint, nm_usuario_prev_p text) RETURNS bigint AS $body$
DECLARE

nr_seq_tipo_func_w	integer;				

BEGIN 
select 	coalesce(max(nr_seq_tipo_funcao),0) 
into STRICT	nr_seq_tipo_func_w 
from 	man_grupo_trab_usuario 
where 	nr_seq_grupo_trab = coalesce(nr_seq_grupo_trabalho_p, nr_seq_grupo_p) 
and 	nm_usuario_param = nm_usuario_prev_p;
if (nr_seq_tipo_func_w = 0) then 
	select 	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_tipo_func_w 
	from 	man_tipo_funcao 
	where 	nr_seq_grupo_trab = coalesce(nr_seq_grupo_trabalho_p, nr_seq_grupo_p) 
	and 	cd_cargo = substr(obter_dados_pf(substr(obter_pessoa_fisica_usuario(nm_usuario_prev_p, 'C'),1,10),'CCR'),1,30);
end if;
return	nr_seq_tipo_func_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
