CREATE OR REPLACE FUNCTION obter_obesidade_escala_padua (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(1);
qt_imc_w	smallint;
sql_w     varchar(300);

BEGIN
	ds_retorno_w := 'N';

	begin
		select coalesce(qt_imc,0) into STRICT qt_imc_w
		from (
			SELECT qt_imc from atendimento_sinal_vital
			where nr_atendimento = nr_atendimento_p
			and (qt_imc IS NOT NULL AND qt_imc::text <> '')
			order by dt_sinal_vital desc
		) alias2 LIMIT 1;
	exception
		when no_data_found then
    begin
			qt_imc_w := 0;
		end;
	end;
	
      begin
      sql_w := 'CALL OBTER_OBESIDADE_MD(:1) INTO :ds_retorno_w';
      EXECUTE sql_w USING IN qt_imc_w,
                                    OUT ds_retorno_w;
        exception
          when others then
            ds_retorno_w := null;
        end;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
