CREATE OR REPLACE FUNCTION obter_objeto_feito_schematic ( nr_seq_dic_objeto_p bigint, nr_seq_objeto_schematic_p bigint, ie_enable_p text default 'N', ie_considera_doc_p text default 'S') RETURNS varchar AS $body$
DECLARE


ie_feito_w			varchar(1) := null;
ie_tipo_objeto_w		varchar(10);
ie_tipo_componente_w		varchar(10);
nr_seq_objeto_schematic_w	objeto_schematic.nr_sequencia%type;
nr_seq_funcao_schematic_w	funcao_schematic.nr_sequencia%type;
ie_event_w			varchar(1);
ie_enabled_w		varchar(1);


BEGIN

nr_seq_objeto_schematic_w := nr_seq_objeto_schematic_p;

select	max(ie_tipo_objeto),
	max(ie_tipo_componente),
	max(nr_seq_funcao_schematic)
into STRICT	ie_tipo_objeto_w,
	ie_tipo_componente_w,
	nr_seq_funcao_schematic_w
from	objeto_schematic
where	nr_sequencia = nr_seq_objeto_schematic_w;

if (ie_tipo_componente_w = 'WDLG') then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_objeto_schematic_w
		from	objeto_schematic a
		where	nr_seq_funcao_schematic = nr_seq_funcao_schematic_w
		and	ie_tipo_obj_button in ('OK', 'AUX')
		and	obter_se_objeto_schem_filho(a.nr_sequencia, nr_seq_objeto_schematic_w) = 'S'  LIMIT 1;
	exception
		when	no_data_found then
			nr_seq_objeto_schematic_w := null;
	end;

	if (nr_seq_objeto_schematic_w IS NOT NULL AND nr_seq_objeto_schematic_w::text <> '') then
		select	max(ie_tipo_objeto),
			max(ie_tipo_componente),
			max(nr_seq_funcao_schematic)
		into STRICT	ie_tipo_objeto_w,
			ie_tipo_componente_w,
			nr_seq_funcao_schematic_w
		from	objeto_schematic
		where	nr_sequencia = nr_seq_objeto_schematic_w;
	end if;
end if;

if (ie_tipo_objeto_w = 'BTN' and coalesce(nr_seq_objeto_schematic_w,0) > 0) or (ie_tipo_componente_w = 'WPOPUP' and ie_tipo_objeto_w = 'C' and coalesce(nr_seq_dic_objeto_p,0) > 0) then

	if (coalesce(nr_seq_dic_objeto_p,0) = 0) then

		if (ie_considera_doc_p = 'S') then
			select 	coalesce(max('S'),'N')
			into STRICT 	ie_event_w
			from 	objeto_schematic_doc a
			where 	a.nr_seq_objeto = nr_seq_objeto_schematic_w
			and ((a.ds_documentacao IS NOT NULL AND a.ds_documentacao::text <> '') or a.ie_event = 'S');

			select 	coalesce(max('S'),'N')
			into STRICT 	ie_enabled_w
			from 	objeto_schematic_doc a
			where 	a.nr_seq_objeto = nr_seq_objeto_schematic_w
			and ((a.ds_documentacao IS NOT NULL AND a.ds_documentacao::text <> '') or a.ie_enable = 'S');
		else
			ie_event_w := 'N';
			ie_enabled_w := 'N';
		end if;

		if (ie_event_w = 'N') then
			select 	coalesce(max('S'),'N')
			into STRICT 	ie_event_w
			from 	obj_schematic_evento a
			where 	a.nr_seq_objeto = nr_seq_objeto_schematic_w
			and	coalesce(a.nr_seq_dic_objeto::text, '') = '';
		end if;

		if (ie_enabled_w = 'N') then
			select 	coalesce(max('S'),'N')
			into STRICT 	ie_enabled_w
			from 	regra_condicao a
			where	nr_seq_objeto = nr_seq_dic_objeto_p;
		end if;

		if (ie_enabled_w = 'S' and ie_event_w = 'S') then
			ie_feito_w	:= 'S';
		elsif (ie_enabled_w = 'S') then
			ie_feito_w	:= 'E';
		elsif (ie_event_w = 'S') then
			ie_feito_w	:= 'C';
		end if;
	else

		if (ie_enable_p = 'S') then
			select 	coalesce(max('S'),'N')
			into STRICT 	ie_feito_w
			from 	regra_condicao a
			where	nr_seq_objeto = nr_seq_dic_objeto_p
			and (nr_seq_objeto_schematic = nr_seq_objeto_schematic_w or coalesce(nr_seq_objeto_schematic::text, '') = '');
		else
			if (ie_considera_doc_p = 'S') then
				select 	coalesce(max('S'),'N')
				into STRICT 	ie_event_w
				from 	dic_objeto_doc a
				where 	a.nr_seq_obj_schematic = nr_seq_objeto_schematic_w
				and  	a.nr_seq_objeto = nr_seq_dic_objeto_p
				and ((a.ds_documentacao IS NOT NULL AND a.ds_documentacao::text <> '') or a.ie_event = 'S');

				select 	coalesce(max('S'),'N')
				into STRICT 	ie_enabled_w
				from 	dic_objeto_doc a
				where 	a.nr_seq_obj_schematic = nr_seq_objeto_schematic_w
				and  	a.nr_seq_objeto = nr_seq_dic_objeto_p
				and ((a.ds_documentacao IS NOT NULL AND a.ds_documentacao::text <> '') or a.ie_enable = 'S');
			else
				ie_event_w := 'N';
				ie_enabled_w := 'N';
			end if;

			if (ie_event_w = 'N') then
				select 	coalesce(max('S'),'N')
				into STRICT 	ie_event_w
				from 	obj_schematic_evento a
				where 	a.nr_seq_dic_objeto = nr_seq_dic_objeto_p
				and     a.nr_seq_objeto = nr_seq_objeto_schematic_w;
			end if;

			if (ie_enabled_w = 'N') then
				select 	coalesce(max('S'),'N')
				into STRICT 	ie_enabled_w
				from 	regra_condicao a
				where	nr_seq_objeto = nr_seq_dic_objeto_p
				and (nr_seq_objeto_schematic = nr_seq_objeto_schematic_w or coalesce(nr_seq_objeto_schematic::text, '') = '');
			end if;

			if (ie_enabled_w = 'S' and ie_event_w = 'S') then
				ie_feito_w	:= 'S';
			elsif (ie_enabled_w = 'S') then
				ie_feito_w	:= 'E';
			elsif (ie_event_w = 'S') then
				ie_feito_w	:= 'C';
			end if;
		end if;
	end if;
end if;

return ie_feito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
