CREATE OR REPLACE FUNCTION obter_objeto_pais_usage_config ( nr_seq_objeto_p bigint, ie_componente_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_objeto_ref_w	dic_objeto_pais.nr_seq_objeto_ref%type;
nr_seq_localidade_w 	integer;

BEGIN
if (ie_componente_p in ('WF','WDF','WCP')) then
	begin
	nr_seq_localidade_w := coalesce(Obter_Nr_Seq_Locale(wheb_usuario_pck.get_nm_usuario), 1);

	if (nr_seq_localidade_w <> 1) then	
		begin
		select	max(nr_seq_objeto_ref)
		into STRICT	nr_seq_objeto_ref_w
		from	dic_objeto_pais
		where	nr_seq_objeto = nr_seq_objeto_p
		and 	cd_pais = nr_seq_localidade_w;
		
		if (coalesce(nr_seq_objeto_ref_w,0) = 0) then
			begin
			select	max(nr_seq_objeto_ref)
			into STRICT	nr_seq_objeto_ref_w
			from	dic_objeto_pais
			where	nr_seq_objeto = nr_seq_objeto_p
			and 	cd_pais = 10;
			end;
		end if;
		end;
	end if;
	end;
end if;
return coalesce(nr_seq_objeto_ref_w,nr_seq_objeto_p);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
