CREATE OR REPLACE FUNCTION obter_objeto_regra_unit (cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_grupo_material_w	integer;
cd_subgrupo_material_w	integer;
cd_classe_material_w	integer;
cd_perfil_w		perfil.cd_perfil%type;


BEGIN

cd_perfil_w := wheb_usuario_pck.get_cd_perfil;

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	max(nm_objeto)
into STRICT	ds_retorno_w
from (	SELECT	max(nm_objeto) nm_objeto
	from	regra_comunic_unitariza a
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_material,cd_material_p) = cd_material_p
	and	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
	and	coalesce(nm_usuario_regra,nm_usuario_p) = nm_usuario_p
	and	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	and	ie_situacao = 'A'
	order 	by cd_material,
		cd_classe_material,
		cd_subgrupo_material,
		cd_grupo_material) alias8 LIMIT 1;

return	upper(ds_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
