CREATE OR REPLACE FUNCTION obter_obj_plano_educ (nr_seq_prescr_proc_p text) RETURNS varchar AS $body$
DECLARE

qt_plano_educacional_w	varchar(10);
ie_objetivo_atingido_w	varchar(10);


BEGIN

select	count(*)
into STRICT	qt_plano_educacional_w
from 	pe_prescr_proc_educ
where	nr_seq_prescr_proc = nr_seq_prescr_proc_p;

if (coalesce(qt_plano_educacional_w,0) = 0) then
	ie_objetivo_atingido_w := 'X';
else
	select 	max(ie_objetivo_atingido)
	into STRICT 	ie_objetivo_atingido_w
	from 	pe_prescr_proc_educ
	where 	nr_sequencia = (SELECT 	max(nr_sequencia)
				from 	pe_prescr_proc_educ
				where 	nr_seq_prescr_proc = nr_seq_prescr_proc_p);
end if;

return ie_objetivo_atingido_w;

end;
