CREATE OR REPLACE FUNCTION obter_obj_schem_padrao ( nr_seq_obj_schematic_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_seq_obj_padrao_p objeto_schematic.nr_seq_obj_padrao%type default null) RETURNS bigint AS $body$
DECLARE


nr_seq_obj_padrao_w	bigint;
qt_regra_w		bigint;


BEGIN

begin
select	1
into STRICT	qt_regra_w
from	obj_schem_order
where	nr_seq_obj_schematic = nr_seq_obj_schematic_p  LIMIT 1;
exception
when others then
	qt_regra_w	:= 0;
end;

if (qt_regra_w >= 1) then
	begin
	select	a.nr_seq_obj_padrao
	into STRICT	nr_seq_obj_padrao_w
	from	obj_schem_order a
	where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
	and	exists (SELECT	1
		from	obj_schem_order_usuario x
		where	x.nr_seq_regra	= a.nr_sequencia
		and	x.nm_usuario_regra = nm_usuario_p)  LIMIT 1;
	exception
	when others then
		begin
		select	a.nr_seq_obj_padrao
		into STRICT	nr_seq_obj_padrao_w
		from	obj_schem_order a
		where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
		and	exists (SELECT	1
			from	obj_schem_order_perfil x
			where	x.nr_seq_regra	= a.nr_sequencia
			and	x.cd_perfil = cd_perfil_p)  LIMIT 1;
		exception
		when others then
			begin
			select	a.nr_seq_obj_padrao
			into STRICT	nr_seq_obj_padrao_w
			from	obj_schem_order a
			where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
			and	exists (SELECT	1
				from	obj_schem_order_estab x
				where	x.nr_seq_regra	= a.nr_sequencia
				and	x.cd_estabelecimento = cd_estabelecimento_p)  LIMIT 1;
			exception
			when others then
				null;
			end;
		end;
	end;
end if;

if (coalesce(nr_seq_obj_padrao_w::text, '') = '') then
    if (nr_seq_obj_padrao_p IS NOT NULL AND nr_seq_obj_padrao_p::text <> '') then
        nr_seq_obj_padrao_w := nr_seq_obj_padrao_p;
    else
        select	max(nr_seq_obj_padrao)
        into STRICT	nr_seq_obj_padrao_w
        from	objeto_schematic
        where	nr_sequencia	= nr_seq_obj_schematic_p;
    end if;
end if;

return nr_seq_obj_padrao_w;

end;
$body$
