CREATE OR REPLACE FUNCTION obter_obrigatoriedade_doc (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_obrigatorio_w 	varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select 	max(IE_OBRIGATORIO)
		into STRICT  	ie_obrigatorio_w
		from 	GAP_TIPO_DOC
		where 	NR_SEQUENCIA = nr_sequencia_p;

end if;

return	ie_obrigatorio_w;

end;
$body$
LANGUAGE PLPGSQL
