CREATE OR REPLACE FUNCTION obter_obriga_just_alerta ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_material_p prescr_material.nr_sequencia%type, cd_api_p alerta_api.cd_api%type ) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w	varchar(1) := 'N';


BEGIN

	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		select	rep_obter_se_obriga_just_int(obter_nome_severidade_lexicomp(a.nr_gravidade), a.cd_api, a.ds_sub_tipo_api)
		  into STRICT	ds_retorno_w
		  from	alerta_api a
		 where	a.nr_prescricao = nr_prescricao_p
		   and	a.nr_seq_material = nr_seq_material_p
		   and	a.cd_api = cd_api_p
		   and 	(a.ie_status_requisicao IS NOT NULL AND a.ie_status_requisicao::text <> '');
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
