CREATE OR REPLACE FUNCTION obter_observacao_caixa_receb (nr_seq_caixa_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_observacao_w	varchar(4000);
se_existe_w	bigint;

 
 

BEGIN 
 
select 	count(*) 
into STRICT	se_existe_w 
from 	adiantamento 
where	nr_seq_caixa_rec = nr_seq_caixa_p;
 
if (se_existe_w > 0) then 
	 
	select max(ds_observacao) 
	into STRICT	ds_observacao_w 
	from 	adiantamento 
	where	nr_seq_caixa_rec = nr_seq_caixa_p;
	 
end if;
 
select 	count(*) 
into STRICT	se_existe_w 
from 	titulo_receber_liq 
where	nr_seq_caixa_rec = nr_seq_caixa_p;
			 
if (se_existe_w > 0) then 
	 
	select max(ds_observacao) 
	into STRICT	ds_observacao_w 
	from 	titulo_receber_liq 
	where	nr_seq_caixa_rec = nr_seq_caixa_p;
	 
end if;
 
if (ds_observacao_w = '') then 
	 
	select max(ds_observacao) 
	into STRICT	ds_observacao_w 
	from 	MOVTO_TRANS_FINANC 
	where	nr_seq_caixa_rec = nr_seq_caixa_p;
	 
end if;
 
RETURN substr(ds_observacao_w,1,4000);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
