CREATE OR REPLACE FUNCTION obter_observacao_ficha_oco_cih ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


						
ds_retorno_w		cih_def_infect.ds_observacao%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then
	
	select max(ds_observacao)
	into STRICT   ds_retorno_w
	from   cih_def_infect
	where  nr_prescricao = nr_prescricao_p
	and    nr_seq_prescr = nr_seq_prescricao_p;
end if;

return	ds_retorno_w;

end;
$body$
