CREATE OR REPLACE FUNCTION obter_observacao_fisico (nr_sequencia_p bigint, ie_tipo_trat_p text) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w		varchar(2000);

BEGIN

	if (ie_tipo_trat_p = 'T') then
    select 	max(ds_observacao_fisico)
    into STRICT	ds_retorno_w
    from 	rxt_planejamento_fisico
    where	nr_seq_rxt_campo = nr_sequencia_p;
	elsif (ie_tipo_trat_p = 'B') then
	select 	max(ds_observacao_fisico)
    into STRICT	ds_retorno_w
    from 	rxt_plan_braqui_fisico
    where	nr_seq_aplic = nr_sequencia_p;
	end if;

    return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
