SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_observacao_item_prescr (ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(4000);


BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	if (ie_tipo_item_p in ('M','S')) then
		select	max(ds_observacao)
		into STRICT	ds_observacao_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;
	elsif (ie_tipo_item_p = 'P') then
		select	max(ds_observacao)
		into STRICT	ds_observacao_w
		from	prescr_procedimento
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;
	elsif (ie_tipo_item_p = 'E') then
		select	max(ds_observacao)
		into STRICT	ds_observacao_w
		from	pe_prescr_proc
		where	nr_seq_prescr = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;
	elsif (ie_tipo_item_p ='ME') then -- Obs do medicamento + observacao da enfermagem
		select	substr(max(ds_observacao) ||
			CASE WHEN coalesce(max(DS_OBSERVACAO_ENF)::text, '') = '' THEN null  ELSE chr(13)||chr(13) END  ||
			max(DS_OBSERVACAO_ENF),1,4000)
		into STRICT	ds_observacao_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;

	end if;
end if;

return ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
