CREATE OR REPLACE FUNCTION obter_observacao_item_retorno (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(4000);
nr_seq_ret_item_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_ret_item_w
from	convenio_retorno_item
where	(ds_observacao IS NOT NULL AND ds_observacao::text <> '')
and	nr_interno_conta = nr_interno_conta_p;

if (nr_seq_ret_item_w <> 0) then
	select	ds_observacao
	into STRICT	ds_observacao_w
	from	convenio_retorno_item
	where	nr_sequencia	= nr_seq_ret_item_w;
end if;

return substr(ds_observacao_w, 1, 255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_observacao_item_retorno (nr_interno_conta_p bigint) FROM PUBLIC;

