CREATE OR REPLACE FUNCTION obter_observacao_prescricao ( nr_atendimento_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(4000);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin

	select 	SubStr(DS_OBSERVACAO,1,4000)
	into STRICT 	ds_observacao_w
	from	PRESCR_MEDICA
	where 	nr_prescricao  = nr_prescricao_p;

	end;
end if;

return ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
