CREATE OR REPLACE FUNCTION obter_observacao_tecnica_pac ( cd_pessoa_fisica_p text, cd_refeicao_p text, dt_dieta_p timestamp, nr_atendimento_p bigint default 0) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_obs_tecnica_w	varchar(255);
ie_obs_tecnica_atendimento_w	varchar(1);


BEGIN
ie_obs_tecnica_atendimento_w := Obter_Param_Usuario(1000, 72, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_obs_tecnica_atendimento_w);

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	mapa_dieta_obs_tec_pf
where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	cd_refeicao 		= cd_refeicao_p
and	((ie_obs_tecnica_atendimento_w <> 'P') or (nr_atendimento = nr_atendimento_p))
and	trunc(dt_referencia)   <= dt_dieta_p;

if (nr_sequencia_w > 0) then

	select	max(ds_obs_tecnica)
	into STRICT	ds_obs_tecnica_w
	from	mapa_dieta_obs_tec_pf
	where	nr_sequencia = nr_sequencia_w	;

end if;

return	ds_obs_tecnica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
