CREATE OR REPLACE FUNCTION obter_observacao_trat_onc ( cd_protocolo_p bigint, nr_seq_medicacao_p bigint, nr_seq_paciente_p bigint, ie_tipo_local_p bigint) RETURNS varchar AS $body$
DECLARE



ds_observacao_w	 	varchar(2000);

BEGIN

if (ie_tipo_local_p = 1) and     -- Utilizado para pegar a observação na função tratamento Oncologico
	(cd_protocolo_p	> 0) then
	Begin

		SELECT 	max(subStr(ds_observacao,1,1999))
		INTO STRICT	ds_observacao_w
		FROM	protocolo_medicacao
		WHERE 	cd_protocolo = cd_protocolo_p
		AND	   	nr_sequencia = nr_seq_medicacao_p;

	end;
elsif (ie_tipo_local_p = 2) and   -- Utilizado para pegar a observação na procedure  GERAR_CICLO_ONCOLOGIA
		(nr_seq_paciente_p > 0) then
	begin

		SELECT 	max(subStr(ds_observacao,1,1999))
		INTO STRICT	ds_observacao_w
		FROM	paciente_setor
		WHERE 	nr_seq_paciente = nr_seq_paciente_p;
	end;
end if;

return ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
