SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_observ_bloq_agenda (cd_agenda_p integer, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(255) := null;
qt_bloqueios_w		integer;
ie_obs_bloqueio_w	varchar(1);
ie_exibe_bloqueio_w	varchar(1);
nr_dia_semana_w		smallint;


BEGIN

ie_obs_bloqueio_w := obter_param_usuario(871, 581, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_obs_bloqueio_w);
ie_exibe_bloqueio_w := obter_param_usuario(871, 580, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_exibe_bloqueio_w);

nr_dia_semana_w := pkg_date_utils.get_WeekDay(dt_agenda_p);

if (ie_exibe_bloqueio_w = 'S') then
	if (ie_obs_bloqueio_w = 'S') then
		select 	substr(ds_observacao,1,255)
		into STRICT	ds_observacao_w
		from (	SELECT 	nr_sequencia,
					ds_observacao
				from 	agenda_bloqueio
				where  	cd_agenda    = cd_agenda_p
				and (coalesce(ie_dia_semana, nr_dia_semana_w) = nr_dia_semana_w  or (coalesce(ie_dia_semana, 0) = 9 and nr_dia_semana_w not in (1,7)))
				and    	dt_agenda_p between dt_inicial and dt_final
				order by nr_sequencia desc) a LIMIT 1;
	else
		select 	substr(obter_valor_dominio(1007, ie_motivo_bloqueio),1,255)
		into STRICT	ds_observacao_w
		from (	SELECT 	nr_sequencia,
					ie_motivo_bloqueio
				from 	agenda_bloqueio
				where  	cd_agenda    = cd_agenda_p
				and (coalesce(ie_dia_semana, nr_dia_semana_w) = nr_dia_semana_w  or (coalesce(ie_dia_semana, 0) = 9 and nr_dia_semana_w not in (1,7)))
				and    	dt_agenda_p between dt_inicial and dt_final
				order by nr_sequencia desc) a LIMIT 1;

	end if;

	if (coalesce(ds_observacao_w::text, '') = '') then
		select 	count(*)
		into STRICT	qt_bloqueios_w
		from 	agenda_bloqueio
		where  	cd_agenda    = cd_agenda_p
		and (coalesce(ie_dia_semana, nr_dia_semana_w) = nr_dia_semana_w  or (coalesce(ie_dia_semana, 0) = 9 and nr_dia_semana_w not in (1,7)))
		and    	dt_agenda_p between dt_inicial and dt_final;

		if (qt_bloqueios_w > 0) then
			ds_observacao_w := ' ';
		end if;
	end if;
end if;

return	ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
