CREATE OR REPLACE FUNCTION obter_obs_agend_quimio_bloq ( dt_agendamento_p timestamp, hr_inicial_dia_p timestamp, hr_final_dia_p timestamp, nr_seq_local_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(255) := '';
ie_dia_semana_w		varchar(255);


BEGIN

ie_dia_semana_w	:= obter_cod_dia_semana(dt_agendamento_p);


select	max(substr(ds_observacao,1,255))
into STRICT	ds_observacao_w
from	qt_bloqueio
where	nr_seq_local 	= nr_seq_local_p
and		trunc(dt_agendamento_p) between trunc(dt_inicial)  and trunc(dt_final)
and		(((dt_dia_semana = ie_dia_semana_w) or ((dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1)))) or coalesce(dt_dia_semana::text, '') = '');

return	ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
