SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_obs_alerta_palm_processo ( nr_seq_processo_p bigint, cd_tipos_obs_adm_p text , ie_tipo_solucao_p bigint , nr_seq_etiqueta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacoes_w	varchar(4000);
ds_obs_w			varchar(4000);
dt_atualizacao_w	timestamp;
nr_prescricao_w		prescr_medica.nr_prescricao%type;
nr_seq_solucao_w	bigint;

cSolucoes CURSOR FOR
	SELECT	distinct b.ds_observacao, b.dt_atualizacao
	from	prescr_solucao_evento b
	where	b.nr_prescricao = nr_prescricao_w
	and		b.ie_alteracao = 9
	and		b.ie_tipo_solucao = ie_tipo_solucao_p
	and		coalesce(b.nr_seq_solucao, b.nr_seq_procedimento, b.nr_seq_material, b.nr_seq_nut, b.nr_seq_nut_neo) = nr_seq_solucao_w
	and		(b.nr_seq_tipo_obs IS NOT NULL AND b.nr_seq_tipo_obs::text <> '')
	and		obter_se_valor_contido(b.nr_seq_tipo_obs, cd_tipos_obs_adm_p) = 'S'	
	and     coalesce(ie_mostra_adep,'S') = 'S'
	and		coalesce(ie_evento_valido,'S') = 'S'
	order by b.dt_atualizacao desc;
	
cItens CURSOR FOR
	SELECT	distinct b.ds_observacao, b.dt_atualizacao
	from	prescr_mat_hor a,
			prescr_mat_alteracao b
	where	a.nr_prescricao = b.nr_prescricao
	and		b.ie_alteracao = 11
	and		a.nr_sequencia = coalesce(b.nr_seq_horario,a.nr_sequencia)
	and		a.nr_seq_material = coalesce(b.nr_seq_prescricao,a.nr_seq_material)
	and		a.nr_seq_processo = nr_seq_processo_p
	and 	a.nr_sequencia = nr_seq_etiqueta_p
	and		(b.nr_seq_tipo_obs IS NOT NULL AND b.nr_seq_tipo_obs::text <> '')
	and		obter_se_valor_contido(b.nr_seq_tipo_obs, cd_tipos_obs_adm_p) = 'S'
	and     coalesce(ie_mostra_adep,'S') = 'S'
	and		coalesce(ie_evento_valido,'S') = 'S'
	order by b.dt_atualizacao desc;


BEGIN

/*
Tipo de solucao:
1 - SOL
2 - SNE
3 - HM
4 - NPA
5 - NPN
6 - NAN
7 - NPP
*/
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (cd_tipos_obs_adm_p IS NOT NULL AND cd_tipos_obs_adm_p::text <> '') then
	select	coalesce(max(nr_prescricao),0),
			coalesce(max(nr_seq_solucao),0)
	into STRICT	nr_prescricao_w,
			nr_seq_solucao_w
	from	adep_processo
	where	nr_sequencia = nr_seq_processo_p;

		if (nr_seq_solucao_w > 0) and (nr_prescricao_w > 0) and (ie_tipo_solucao_p > 0) then
			open cSolucoes;
			loop
			fetch cSolucoes into	
				ds_obs_w,
				dt_atualizacao_w;
			EXIT WHEN NOT FOUND; /* apply on cSolucoes */
				begin
				ds_observacoes_w	:= substr(ds_observacoes_w || '*' || ds_obs_w || chr(10),1,2000);
				end;
			end loop;
			close cSolucoes;
		elsif (nr_seq_etiqueta_p IS NOT NULL AND nr_seq_etiqueta_p::text <> '') then
			open cItens;
			loop
			fetch cItens into	
				ds_obs_w,
				dt_atualizacao_w;
			EXIT WHEN NOT FOUND; /* apply on cItens */
				begin
				ds_observacoes_w	:= substr(ds_observacoes_w || '*' || ds_obs_w || chr(10),1,2000);
				end;
			end loop;
			close cItens;
		end if;
end if;

return	ds_observacoes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_obs_alerta_palm_processo ( nr_seq_processo_p bigint, cd_tipos_obs_adm_p text , ie_tipo_solucao_p bigint , nr_seq_etiqueta_p bigint) FROM PUBLIC;

