CREATE OR REPLACE FUNCTION obter_obs_anterior_servico ( dt_referencia_p timestamp, nr_atendimento_p bigint, nr_seq_servico_p bigint, ie_opcao_p text default 'O') RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(4000);
ds_observacao_tec_w	varchar(255);
ds_retorno_w		varchar(4000);
nr_servico_w		bigint;
dt_servico_w		timestamp;

/*
O - Observação
T - Observação técnica
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND nr_seq_servico_p IS NOT NULL AND nr_seq_servico_p::text <> '') then

	select	max(b.dt_servico)
	into STRICT	dt_servico_w
	from	nut_atend_serv_dia b
	where	b.nr_atendimento = nr_atendimento_p
	and	b.nr_seq_servico = nr_seq_servico_p
	and	b.dt_servico < dt_referencia_p;

	select	max(a.ds_observacao),
		max(a.ds_observacao_tec)
	into STRICT	ds_observacao_w,
		ds_observacao_tec_w
	from  	nut_atend_serv_dia a
	where	a.nr_seq_servico = nr_seq_servico_p
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_servico < dt_referencia_p
	and	a.dt_servico between inicio_dia(dt_servico_w) and fim_dia(dt_servico_w);
end if;

if (ie_opcao_p = 'T') then
	ds_retorno_w := ds_observacao_tec_w;
else
	ds_retorno_w := ds_observacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
