CREATE OR REPLACE FUNCTION obter_obs_dietas_vipe ( nr_prescricoes_p text, cd_item_p text, cd_intervalo_p text, ie_status_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000)	:= '';
ds_observacao_w		prescr_dieta.ds_observacao%type;

c01 CURSOR FOR
SELECT 	ds_observacao
from 	prescr_dieta
where 	nr_prescricao in (WITH RECURSIVE cte AS (
select (regexp_substr(nr_prescricoes,'[^,]+', 1, 1))::numeric
                          from (select nr_prescricoes_p nr_prescricoes )(regexp_substr(nr_prescricoes, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_prescricoes, '[^,]+', 1, level))::text <> '')  UNION ALL
select (regexp_substr(nr_prescricoes,'[^,]+', 1, (c.level+1)))::numeric 
                          from (select nr_prescricoes_p nr_prescricoes ) JOIN cte c ON ()

) SELECT * FROM cte;
)
and 	cd_dieta = somente_numero(cd_item_p)
and 	coalesce(cd_intervalo,'XPTO') = coalesce(cd_intervalo_p,'XPTO')
and		coalesce(ie_suspenso, 'N')	= coalesce(ie_status_p, coalesce(ie_suspenso, 'N'));

c02 CURSOR FOR
SELECT 	distinct
		a.ds_observacao
from 	prescr_dieta a,
		prescr_dieta_hor b
where 	a.nr_prescricao = b.nr_prescricao
and 	a.nr_prescricao in (WITH RECURSIVE cte AS (
select (regexp_substr(nr_prescricoes,'[^,]+', 1, 1))::numeric
                          from (select nr_prescricoes_p nr_prescricoes )(regexp_substr(nr_prescricoes, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_prescricoes, '[^,]+', 1, level))::text <> '')  UNION ALL
select (regexp_substr(nr_prescricoes,'[^,]+', 1, (c.level+1)))::numeric 
                          from (select nr_prescricoes_p nr_prescricoes ) JOIN cte c ON ()

) SELECT * FROM cte;
)
and 	b.cd_refeicao = cd_item_p
and 	coalesce(cd_intervalo,'XPTO') = coalesce(cd_intervalo_p,'XPTO')
and		coalesce(ie_suspenso, 'N')	= coalesce(ie_status_p, coalesce(ie_suspenso, 'N'))
and		coalesce(ds_retorno_w::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		begin
			if (length(coalesce(ds_observacao_w, '')) > 0) then
				ds_retorno_w	:= ds_retorno_w || ' ' || ds_observacao_w;
			end if;
		exception when others then
			null;
		end;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
		begin
			if (length(coalesce(ds_observacao_w, '')) > 0) then
				ds_retorno_w	:= ds_retorno_w || ' ' || ds_observacao_w;
			end if;
		exception when others then
			null;
		end;
	end;
end loop;
close C02;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
