CREATE OR REPLACE FUNCTION obter_obs_dieta_prescr_atend ( nr_atendimento_p bigint, qt_hora_p bigint) RETURNS varchar AS $body$
DECLARE



nr_prescricao_w		bigint;
ds_observacao_w		varchar(4000):= '';
cd_dieta_w		bigint;


BEGIN

select	coalesce(max(a.nr_prescricao),0)
into STRICT	nr_prescricao_w
from 	prescr_dieta b,
	prescr_medica a
where 	a.nr_prescricao 	= b.nr_prescricao
and 	a.nr_atendimento	= nr_atendimento_p
and 	a.dt_prescricao	> clock_timestamp() - (qt_hora_p / 24);

if (nr_prescricao_w > 0) then

	select	coalesce(max(cd_dieta),0)
	into STRICT	cd_dieta_W
	from	prescr_dieta
	where	nr_prescricao 	= nr_prescricao_w
	and 	ie_suspenso	= 'N';

	select	max(ds_observacao)
	into STRICT	ds_observacao_w
	from	prescr_dieta
	where	cd_dieta 	= cd_dieta_w
	and	nr_prescricao 	= nr_prescricao_w
	and 	ie_suspenso	= 'N';

end if;

return ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
