CREATE OR REPLACE FUNCTION obter_obs_padrao_gedipa ( cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(2000);
ds_titulo_w	varchar(40);
ds_retorno_w	varchar(2000);


BEGIN

select	max(ds_titulo),
	max(ds_observacao)
into STRICT	ds_titulo_w,
	ds_observacao_w
from	observacao_padrao_gedipa
where	nr_sequencia	=	(	SELECT	max(nr_seq_observacao)
					from	regra_obs_padrao_gedipa
					where	cd_material	=	cd_material_p);

return	substr(upper(ds_titulo_w) || chr(13) || chr(10) || ds_observacao_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
