CREATE OR REPLACE FUNCTION obter_obs_prescr_material ( nr_prescricoes_p text, cd_item_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ds_observacao_w varchar(4000);
nr_prescricao_w	bigint;

BEGIN
if (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	begin
	nr_prescricao_w	:=	obter_max_nrprescricao(nr_prescricoes_p);

	select	max(ds_observacao)
	into STRICT   	ds_observacao_w
	from   	prescr_material
	where  	nr_prescricao 	= nr_prescricao_w
	and	cd_material	= cd_item_p
	and	cd_intervalo	= cd_intervalo_p;
	end;
end if;
return ds_observacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
