CREATE OR REPLACE FUNCTION obter_obs_proc_prescr (nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(2000);


BEGIN

select	max(ds_observacao)
into STRICT	ds_observacao_w
from	procedimento_prescricao
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;


select	coalesce(max(ds_observacao), ds_observacao_w)
into STRICT	ds_observacao_w
from	proc_interno
where	nr_sequencia = nr_seq_proc_interno_p;


return	ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
