CREATE OR REPLACE FUNCTION obter_obs_servico_home_care (nr_seq_servico_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	substr(max(a.ds_observacao),1,255)
into STRICT	ds_retorno_w
from	hc_paciente_servico a,
	paciente_home_care b
where	a.nr_seq_pac_home_care = b.nr_sequencia
and	b.cd_pessoa_fisica     = cd_pessoa_fisica_p
and	nr_seq_servico	     = nr_seq_servico_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
