CREATE OR REPLACE FUNCTION obter_obs_solic_ordem (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_solic_compra_w		bigint;			
ds_observacao_w		varchar(2000);
			

BEGIN 
 
select	coalesce(max(nr_solic_compra),0) 
into STRICT	nr_solic_compra_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (nr_solic_compra_w > 0) then 
	select	ds_observacao 
	into STRICT	ds_observacao_w 
	from	solic_compra 
	where	nr_solic_compra = nr_solic_compra_w;
 
end if;
 
return	ds_observacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
