CREATE OR REPLACE FUNCTION obter_obs_tecnica_mapa (nr_atendimento_p bigint, dt_dieta_p timestamp, cd_refeicao_p text, ie_destino_dieta_p text default 'P') RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN
	select	max(coalesce(ds_observacao_tec,''))
	into STRICT	ds_retorno_w
	from	mapa_dieta
	where	nr_atendimento = nr_atendimento_p
	and	trunc(dt_dieta) = trunc(dt_dieta_p)
	and	cd_refeicao = cd_refeicao_p
	and	ie_destino_dieta = ie_destino_dieta_p;


return	ds_retorno_w;

end;
$body$
