CREATE OR REPLACE FUNCTION obter_obs_tec_dieta_atend ( nr_atendimento_p bigint, cd_unidade_basica_p text, cd_refeicao_p text) RETURNS varchar AS $body$
DECLARE



ds_observacao_w		varchar(2000):= '';


BEGIN

select	substr(ds_observacao_tec,1,2000)
into STRICT	ds_observacao_w
from	mapa_dieta
where	nr_sequencia = (SELECT	max(m.nr_sequencia)
			from	mapa_dieta m
			where	nr_atendimento 		= nr_atendimento_p
			and	cd_unidade_basica 	= cd_unidade_basica_p
			and	cd_refeicao 		= cd_refeicao_p);

return ds_observacao_w;

end;
$body$
