CREATE OR REPLACE FUNCTION obter_obs_tipo_isolamento_cid (cd_doenca_cid_p text, ie_opcao_p bigint, ie_opcao_ret_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(4000);
ds_tipo_isolamento_w	varchar(80);
ds_observacao_w		varchar(2000);
ie_quebra_w		varchar(1);

c01 CURSOR FOR
	SELECT	distinct
		ds_tipo_isolamento,
		ds_observacao
	from	cih_doenca_infecto a,
		cih_tipo_isolamento b
	where	a.nr_seq_tipo_isolamento = b.nr_sequencia
	and	cd_doenca_cid	= cd_doenca_cid_p;


BEGIN

ie_quebra_w	:= 'N';
OPEN C01;
LOOP
FETCH C01 into
		ds_tipo_isolamento_w,
		ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_opcao_p = 0) then
		if (ie_quebra_w = 'S') then
			ds_resultado_w := ds_resultado_w || ' - ';
		end if;
	else
		if (ie_quebra_w = 'S') then
			ds_resultado_w := ds_resultado_w || chr(10);
		end if;
	end if;

	if (ie_opcao_ret_p = 1) then
		ds_resultado_w := ds_resultado_w || ds_tipo_isolamento_w;
	else
		ds_resultado_w := ds_resultado_w || ds_observacao_w;
	end if;

	ie_quebra_w := 'S';

END LOOP;
CLOSE C01;

return ds_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
