CREATE OR REPLACE FUNCTION obter_obs_titulo_tributo ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w			varchar(4000);
nm_pessoa_w			varchar(80);
nr_seq_nota_fiscal_w		bigint;
ds_observacao_ww		varchar(20000);


BEGIN
begin
select	substr(obter_nome_pf_pj(b.cd_pessoa_fisica, b.cd_cgc), 1,80),
	nr_seq_nota_fiscal,
	ds_observacao_titulo
into STRICT	nm_pessoa_w,
	nr_seq_nota_fiscal_w,
	ds_observacao_w
from	titulo_pagar b,
	Titulo_pagar_imposto a
where	a.nr_titulo	= b.nr_titulo
and	a.nr_sequencia= nr_sequencia_p;
exception
when others then
	nm_pessoa_w 		:= null;
	nr_seq_nota_fiscal_w 	:= null;
	ds_observacao_w 	:= null;
end;

if (coalesce(nm_pessoa_w::text, '') = '') and (coalesce(nr_seq_nota_fiscal_w::text, '') = '') and (coalesce(ds_observacao_w::text, '') = '') then
	ds_observacao_ww := '';
else
	ds_observacao_ww := substr(coalesce(nm_pessoa_w,''),1,80);

	if (nr_seq_nota_fiscal_w IS NOT NULL AND nr_seq_nota_fiscal_w::text <> '') then
		ds_observacao_ww := ds_observacao_ww || '. ' || obter_desc_expressao(312873) || ': ' || nr_seq_nota_fiscal_w;
	end if;

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		ds_observacao_ww := ds_observacao_ww ||chr(13) || chr(10) || obter_desc_expressao(729518) || ds_observacao_w;
	end if;

end if;

return	substr(ds_observacao_ww,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
