CREATE OR REPLACE FUNCTION obter_ocorrencia_envio_cre ( ie_tipo_ocorrencia_p bigint, cd_banco_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_tipo_ocorrencia_p. Isso está definido no atributo  IE_TIPO_OCORRENCIA da tabela BANCO_OCORRENCIA_ESCRITURAL
1 = Entrada de título
2 = Baixa de título
4 = Concessão de abatimento
5 = Cancelamento de abatimento
6 = Alteração de vencimento
7 = Alteração número conta cedente
8 = Alteração do seu número
9 = Protestar
18 = Sustar protesto
98 = Não protestar
*/
cd_ocorrencia_w		banco_ocorrencia_escritural.cd_ocorrencia%type;


BEGIN

if (ie_tipo_ocorrencia_p IS NOT NULL AND ie_tipo_ocorrencia_p::text <> '') and (cd_banco_p IS NOT NULL AND cd_banco_p::text <> '') then

	select	max(a.cd_ocorrencia)
	into STRICT	cd_ocorrencia_w
	from	banco_ocorrencia_escritural a
	where	a.cd_banco				= cd_banco_p
	and		a.ie_tipo_ocorrencia	= ie_tipo_ocorrencia_p;

end if;

return	substr(cd_ocorrencia_w,1,3);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
