CREATE OR REPLACE FUNCTION obter_ocorrencia_interv_dieta ( cd_intervalo_p text, qt_hora_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
	O	Ocorrências
	H	Horas de duração de cada intervalo
*/
nr_ocorrencia_w		double precision;
qt_hora_w		double precision;
qt_operacao_w		intervalo_prescricao.qt_operacao%type;
ie_operacao_w		varchar(1);


BEGIN

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

	select	coalesce(max(qt_operacao),1),
		coalesce(max(ie_operacao),'F')
	into STRICT	qt_operacao_w,
		ie_operacao_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p;

	if (ie_operacao_w = 'X') then
		nr_ocorrencia_w	:= dividir(24,qt_operacao_w); --Ceil(qt_hora_p / (24 / qt_operacao_w));
		qt_hora_w	:= dividir(24,qt_operacao_w);

	elsif (ie_operacao_w = 'H') then
		qt_hora_w	:= qt_operacao_w;
		nr_ocorrencia_w	:= qt_operacao_w;

	elsif (ie_operacao_w in ('F','V')) then
		nr_ocorrencia_w	:= qt_operacao_w;
		qt_hora_w	:= dividir(24,qt_operacao_w);
	else
		nr_ocorrencia_w	:= qt_operacao_w;
		qt_hora_w	:= dividir(24,qt_operacao_w);
	end if;

end if;

if (ie_opcao_p = 'O') then
	return nr_ocorrencia_w;
else
	return qt_hora_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
