CREATE OR REPLACE FUNCTION obter_ocorrencia_rep ( cd_intervalo_p text, cd_setor_p bigint, cd_material_p bigint default null) RETURNS bigint AS $body$
DECLARE



qt_se_necessario_w	smallint;
ie_mesmo_zerado_w	varchar(1);

BEGIN

Select	coalesce(max(qt_se_necessario),0),
	coalesce(max(ie_mesmo_zerado),'N')
into STRICT	qt_se_necessario_w,
	ie_mesmo_zerado_w
from	intervalo_setor
where	cd_intervalo 		= cd_intervalo_p
and	cd_setor_atendimento 	= cd_setor_p
and	((coalesce(cd_material::text, '') = '') or (cd_material = cd_material_p));

if (qt_se_necessario_w = 0) and (ie_mesmo_zerado_w = 'N') then

	Select	coalesce(max(qt_se_necessario),1)
	into STRICT	qt_se_necessario_w
	from 	intervalo_prescricao
	where 	cd_intervalo = cd_intervalo_p;
end if;

return	qt_se_necessario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
